        {
            tablesize++;
        }
    }
    //å‡ºç°å­—ç¬¦çš„ä¸ªæ•° (int)
    fwrite(&tablesize, sizeof(int), 1, f);
    size_metadata += sizeof(int);
    //wpl
    fwrite(&wpl, sizeof(int), 1, f);
    size_metadata += sizeof(int);

    for (size_t i = 0; i < 255; i++)
    {
        if (htable[i])
        {
            //è¯¥å­—ç¬¦
            fwrite(&i, sizeof(char), 1, f);
            size_metadata += sizeof(char);
            //è¯¥å­—ç¬¦å¯¹åº”çš„Huffmanç¼–ç 
            fwrite(htable[i], sizeof(huffmanTable), 1, f);
            size_metadata += sizeof(huffmanTable);
        }
    }

    unsigned int sum = 0, k = 0;
    char buf[1024];

    // åˆå§‹åŒ–
    int len = fread(buf, sizeof(char), sizeof(buf), src);
    size_real += len;
    huffmanTable *t = htable[buf[0]];
    int start = t->start;
    int toLen = sizeof(int) * 8;

    while (1)
    {
        if (toLen == 0)
        {
            //å¦‚æœ toLen,32 ä½ éƒ½å·²å¡«å……å®Œåˆ™,å†™å…¥sumåˆ°æ–‡ä»¶, å¹¶é‡ç½® sum,toLen
            toLen = sizeof(int) * 8;
            fwrite(&sum, sizeof(sum), 1, f);
            size_data += sizeof(int);
            sum = 0;
        }

        if (start == 0)
        {
            //è‹¥æŸä¸ªå­—ç¬¦ å·²å†™å®Œ,åˆ™K++, æ›´æ¢ä¸‹ä¸€ä¸ªå­—ç¬¦
            ++k;
            //è‹¥bufç¼“å†²åŒºå·²è¯»å®Œ,åˆ™ç»§ç»­ä»æ–‡ä»¶ä¸­è¯»ä¸‹ä¸€ä¸ª
            if (k == len)
            {
                //è‹¥æ–‡ä»¶å·²è¯»å®Œåˆ™è·³å‡ºå¾ªç¯,whileå¾ªç¯å”¯ä¸€å‡ºå£
                if ((len = fread(buf, sizeof(char), sizeof(buf), src)) <= 0)
                {
                    break;
                }
                //ç¼“å†²åŒºè¯»å…¥ä¹‹å,é‡ç½®k
                k = 0;
                size_real += len;
            }
            //é‡ç½® t,start
            t = htable[buf[k]];
            start = t->start;
        }
        /*
            32ä½æ— ç¬¦å·æ•´å‹ä¸­å¯¹åº”çš„æƒé‡: 1 << toLen - 1
            åˆ¤æ–­ç¼–ç  åœ¨startä½çš„ 0æˆ–1: (t->val >> start - 1) & 1 
        */
        sum += (1 << toLen - 1) * ((t->val >> start - 1) & 1);
        toLen--;
        start--;
    }
    // é€€å‡ºå è¿˜å‰©æœ€åä¸€ä¸ªæ²¡å†™å®Œ
    fwrite(&sum, sizeof(sum), 1, f);
    size_data += sizeof(int);

