class Solution {
public:
    int evalRPN(vector<string> &tokens) {
        int i=0;
        stack<int> s;
        int ret=0;
        while(i<tokens.size()){
            while(i<tokens.size() && tokens[i]!="+" && tokens[i]!="-" && tokens[i]!="*" && tokens[i]!="/")
                s.push(atoi(tokens[i++].c_str()));
            int right_num=s.top();
            s.pop();
            if(s.empty())
                return right_num;
            int left_num=s.top();
            s.pop();
            if(tokens[i]=="+")
                ret=left_num+right_num;
            else if(tokens[i]=="-")
                ret=left_num-right_num;
            else if(tokens[i]=="*")
                ret=left_num*right_num;       
            else if(tokens[i]=="/")
                ret=left_num/right_num;
            s.push(ret);
            i++;
        }
        return s.top();
    }
};

